import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cotizador() {
    const [year, setYear] = useState('');
    const [marca, setMarca] = useState('');
    const [modelo, setModelo] = useState('');
    const [marcas, setMarcas] = useState([]);
    const [modelos, setModelos] = useState([]);
    const [precio, setPrecio] = useState(null);
    const [maxAFinanciar, setMaxAFinanciar] = useState(null);
    const [minAFinanciar, setMinAFinanciar] = useState(null);
    const [selectedProduct, setSelectedProduct] = useState('a');
    const [cuotas, setCuotas] = useState([]);

    useEffect(() => {
        // Fetch minAFinanciar from calculadora.txt
        const fetchMinAFinanciar = async () => {
            try {
                const response = await axios.get('http://localhost:5000/files/calculadora.txt');
                const data = response.data;
                setMinAFinanciar(data.minAFinanciar.valor);
            } catch (error) {
                console.error('Error fetching minAFinanciar:', error);
            }
        };
        fetchMinAFinanciar();
    }, []);

    const fetchMarcas = async (year) => {
        try {
            const response = await axios.post('http://localhost/php/curl.php', { year, action: 'getBrandsByYear' });
            setMarcas(response.data.brands);
        } catch (error) {
            console.error('Error fetching marcas:', error);
        }
    };

    const fetchModelos = async (idMarca, year) => {
        try {
            const response = await axios.post('http://localhost/php/curl.php', { idMarca, year, action: 'getModelsByBrand' });
            setModelos(response.data.models);
        } catch (error) {
            console.error('Error fetching modelos:', error);
        }
    };

    const fetchPriceCar = async (codia, year) => {
        try {
            const response = await axios.post('http://localhost/php/curl.php', { codia, year, action: 'getPriceByCodia' });
            setPrecio(response.data.price);
            calculateMaxAFinanciar(response.data.price, year);
        } catch (error) {
            console.error('Error fetching price:', error);
        }
    };

    const calculateMaxAFinanciar = (precio, year) => {
        let maxAFinanciar;
        if (selectedProduct === 'a') {
            if (year <= 2025 && year >= 2020) {
                maxAFinanciar = precio * 0.7;
            } else if (year <= 2019 && year >= 2015) {
                maxAFinanciar = precio * 0.6;
            } else if (year <= 2014 && year >= 2012) {
                maxAFinanciar = precio * 0.5;
            }
        } else {
            if (year <= 2024 && year >= 2020) {
                maxAFinanciar = precio * 0.7;
            } else if (year <= 2019 && year >= 2015) {
                maxAFinanciar = precio * 0.7;
            } else if (year <= 2014 && year >= 2002) {
                maxAFinanciar = precio * 0.5;
            }
        }
        setMaxAFinanciar(maxAFinanciar);
    };

    const handleYearChange = (e) => {
        setYear(e.target.value);
        fetchMarcas(e.target.value);
    };

    const handleMarcaChange = (e) => {
        setMarca(e.target.value);
        fetchModelos(e.target.value, year);
    };

    const handleModeloChange = (e) => {
        setModelo(e.target.value);
        fetchPriceCar(e.target.value, year);
    };

    const handleProductChange = (e) => {
        setSelectedProduct(e.target.value);
        calculateMaxAFinanciar(precio, year);
    };

    const calcularCuota = (montoPrincipal, tasaInteresAnual, plazo) => {
        const interesMensual = (tasaInteresAnual / 100) / 12;
        const factor = Math.pow(1 + interesMensual, plazo);
        const cuotaMensual = montoPrincipal * interesMensual * factor / (factor - 1);
        return cuotaMensual;
    };

    const desglosarCuotas = (montoPrincipal, tasaInteresAnual, plazo, feeValor) => {
        const interesMensual = (tasaInteresAnual / 100) / 12;
        const comision = montoPrincipal * (feeValor * 1.21);
        let saldo = montoPrincipal + comision;
        const cuotaMensual = calcularCuota(saldo, tasaInteresAnual, plazo);
        const desglose = [];
        let basePromedio = 0;
        let cuotaPromedio = 0;

        for (let i = 1; i <= plazo; i++) {
            const interesCuota = saldo * interesMensual;
            const iva = interesCuota * 0.21;
            const capitalCuota = cuotaMensual - interesCuota;
            const cuotaConIva = cuotaMensual + iva;
            saldo -= capitalCuota;

            basePromedio += cuotaConIva;
        }

        cuotaPromedio = basePromedio / plazo;
        return parseFloat(cuotaPromedio.toFixed(0)).toLocaleString('es-AR', { minimumFractionDigits: 0, maximumFractionDigits: 0 });
    };

    return (
        <div className="content">
            <h1>Cotizador</h1>
            <form>
                <div>
                    <label>Año:</label>
                    <select value={year} onChange={handleYearChange}>
                        <option value="" disabled>Seleccione un año</option>
                        <option value="2025">2025</option>
                        <option value="2024">2024</option>
                        <option value="2023">2023</option>
                        <option value="2022">2022</option>
                        <option value="2021">2021</option>
                        <option value="2020">2020</option>
                        <option value="2019">2019</option>
                        <option value="2018">2018</option>
                        <option value="2017">2017</option>
                        <option value="2016">2016</option>
                        <option value="2015">2015</option>
                        <option value="2014">2014</option>
                        <option value="2013">2013</option>
                        <option value="2012">2012</option>
                    </select>
                </div>
                <div>
                    <label>Marca:</label>
                    <select value={marca} onChange={handleMarcaChange}>
                        <option value="" disabled>Seleccione una marca</option>
                        {marcas.map((marca) => (
                            <option key={marca.id} value={marca.id}>{marca.name}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Modelo:</label>
                    <select value={modelo} onChange={handleModeloChange}>
                        <option value="" disabled>Seleccione un modelo</option>
                        {modelos.map((modelo) => (
                            <option key={modelo.codia} value={modelo.codia}>{modelo.modelo}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Producto:</label>
                    <select value={selectedProduct} onChange={handleProductChange}>
                        <option value="a">Producto A</option>
                        <option value="b">Producto B</option>
                    </select>
                </div>
                <div>
                    <label>Precio:</label>
                    <input type="text" value={precio ? `$${precio.toLocaleString('es-AR')}` : ''} readOnly />
                </div>
                <div>
                    <label>Máximo a Financiar:</label>
                    <input type="text" value={maxAFinanciar ? `$${maxAFinanciar.toLocaleString('es-AR')}` : ''} readOnly />
                </div>
                <div>
                    <label>Mínimo a Financiar:</label>
                    <input type="text" value={minAFinanciar ? `$${minAFinanciar.toLocaleString('es-AR')}` : ''} readOnly />
                </div>
                <div>
                    <label>Cuotas:</label>
                    <ul>
                        {cuotas.map((cuota, index) => (
                            <li key={index}>{cuota}</li>
                        ))}
                    </ul>
                </div>
            </form>
        </div>
    );
}

export default Cotizador;
