function Dashboard() {
  const handleLogout = async () => {
    await axios.post(
      "http://localhost:5000/api/logout",
      {},
      { withCredentials: true }
    );
    window.location.reload();
  };

  return (
    <div>
      <nav className="navbar">
        <img src="/lever.svg" alt="Lever Logo" className="logo" />
        <ul className="nav-links">
          <li>
            <Link to="/dashboard/tablero">Tablero</Link>
          </li>
          <li>
            <Link to="/dashboard/cotizar">Cotizar</Link>
          </li>
          <li>
            <Link to="/dashboard/operaciones">Operaciones</Link>
          </li>
          <li>
            <Link to="/dashboard/instructivos">Instructivos</Link>
          </li>
        </ul>
        <button className="Cs" onClick={handleLogout}>
          Cerrar Sesión
        </button>
      </nav>
      <Routes>
        <Route path="tablero" element={<Tablero />} />
        <Route path="cotizar" element={<Cotizar />} />
        <Route path="operaciones" element={<Operaciones />} />
        <Route path="instructivos" element={<Instructivos />} />
      </Routes>
    </div>
  );
}

export default Dashboard;
